    _refresh_btn_begin_endorse : function() {
        if (er_endorse_endorser.is_endorser_switched_on()) {
            this.jq_btn_begin_endorse.addClass('started');
        } else {
            this.jq_btn_begin_endorse.removeClass('started');
        }
    },

