                return;

            localStorage.er_connect_selected_list_active_id = id;

            var evt = document.createEvent("CustomEvent");
            evt.initCustomEvent(this.EVENT_ACTIVE_CONNECT_SELECTED_SWITCHED, true, true, id);
            window.dispatchEvent(evt);
