                var val = this.jq_search_status_value.val();
                if (val == '')
                    val = '.';
                else if (val == '.')
                    val = '..';
                else if (val == '..')
                    val = '...';
                else if (val == '...')
                    val = '';
                this.jq_search_status_value.val();

                setTimeout(this.closure_status_search_animation, 500);
